require 'fileutils'\n\ndef copy_with_progress(source, destination)\n  total_size = File.size(source)\n  copied_size = 0\n  buffer_size = 1024 * 1024 # 1MB\n\n  File.open(source, "rb") do |s|\n    File.open(destination, "wb") do |d|\n      while chunk = s.read(buffer_size)\n        d.write(chunk)\n        copied_size += chunk.size\n        progress(copied_size, total_size)\n      end\n    end\n  end\nend\n\ndef progress(current, total)\n  percent = (current.to_f / total * 100).round(2)\n  print "\rCopied #{percent}% (#{current}/#{total} bytes)"\n  $stdout.flush\nend\n\n# Usage\nsource_path = 'path/to/source/file'\ndestination_path = 'path/to/destination/file'\ncopy_with_progress(source_path, destination_path)\nputs "\nCopy completed!"