# Controller Action (app/controllers/items_controller.rb)\ndef create\n  @item = Item.new(item_params)\n  respond_to do |format|\n    if @item.save\n      format.html { redirect_to items_path, notice: 'Item was successfully created.' }\n      format.js   # This would look for a file called create.js.erb in views/items/\n    else\n      format.html { render action: "new" }\n      format.js\n    end\n  end\nend\n\n# JavaScript Response (app/views/items/create.js.erb)\n$("#items").append("<%= j render @item %>");