# spec/controllers/your_controller_spec.rb (continued)\ndescribe "when signed in" do\n  let(:user) { FactoryBot.create(:user) }\n  let(:your_object) { FactoryBot.create(:your_object, user: user) }\n\n  before { log_in(user) }\n\n  describe "GET #index" do\n    it "should assign all objects for the signed-in user" do\n      get :index\n      expect(assigns(:objects)).to eq([your_object])\n    end\n  end\n\n  describe "GET #show" do\n    it "renders a successful response if the object belongs to the user" do\n      get :show, params: { id: your_object.id }\n      expect(response).to be_successful\n    end\n\n    it "redirects to root path if the object does not belong to the user" do\n      other_user = FactoryBot.create(:user)\n      log_in(other_user)\n\n      get :show, params: { id: your_object.id }\n      expect(response).to redirect_to(root_path)\n    end\n  end\n\n  # Add more authenticated test cases as needed\nend