# Example RSpec test for an EmailService class method\nrequire 'rails_helper'\n\nRSpec.describe EmailService, type: :service do\n  describe '#send_email' do\n    it "sends an email" do\n      recipient = create(:user)\n      expect {\n        described_class.send_email(recipient.email, "Subject", "Body")\n      }.to change { ActionMailer::Base.deliveries.count }.by(1)\n\n      last_email = ActionMailer::Base.deliveries.last\n      expect(last_email.to).to eq([recipient.email])\n      expect(last_email.subject).to eq("Subject")\n      expect(last_email.body.encoded).to include("Body")\n    end\n  end\nend