# In routes.rb\nresources :users do\n  member do\n    put 'activate'\n  end\nend\n\n# Controller action:\ndef activate\n  @user = User.find(params[:id])\n  if @user.update(active: true)\n    render json: { message: "User activated" }, status: :ok\n  else\n    render json: { error: @user.errors.full_messages.join(", ") }, status: :unprocessable_entity\n  end\nend\n\n# For general update:\ndef update\n  @user = User.find(params[:id])\n  if @user.update(user_params)\n    render json: @user, status: :ok\n  else\n    render json: { error: @user.errors.full_messages.join(", ") }, status: :unprocessable_entity\n  end\nend\n\nprivate\n\ndef user_params\n  params.require(:user).permit(:email, :name, ...) # list all permitted attributes\nend