require 'mysql2'\n\n# Connect to your MySQL database\nclient = Mysql2::Client.new(\n  host: "localhost",\n  username: "your_username",\n  password: "your_password",\n  database: "your_database"\n)\n\n# Execute the SQL command\nresult = client.query("SHOW CREATE TABLE your_table_name")\n\n# Fetch the result as a hash and get the create table statement\ncreate_statement = result.first["Create Table"]\n\nputs create_statement\n\n# Example of how you might delete an index/key from the create statement if it exists\nif create_statement.include?("KEY `your_key_name`")\n  # Here, you could manipulate the string to remove the key declaration\n  modified_create_statement = create_statement.sub(/,\s*KEY `your_key_name`\s*\([^)]+\)/i, '')\n  puts "Modified Create Statement:\n#{modified_create_statement}"\nend\n\n# Don't forget to close the client connection when done\nclient.close