# app/models/user.rb\nclass User < ApplicationRecord\n  attr_accessor :humanproof_answer # This allows you to use this attribute without persisting it in the database\n\n  validates :humanproof_answer, presence: true\n  validate :check_humanproof\n\n  def check_humanproof\n    # Assuming the math question is "2 + 2", the correct answer should be 4.\n    unless humanproof_answer.to_i == 4\n      errors.add(:humanproof_answer, "is not the right answer")\n    end\n  end\nend