class AssetsController < ApplicationController\n     before_action :authenticate_user!\n\n     def download_swf\n       file_path = Rails.root.join('app', 'assets', 'flash', 'your_file.swf')\n       if File.exist?(file_path)\n         send_file file_path, type: "application/x-shockwave-flash", disposition: "inline"\n       else\n         render plain: "File not found", status: 404\n       end\n     end\n   end