class YourModelsController < ApplicationController\n  def step1\n    @your_model = YourModel.new(step1_params)\n    if @your_model.valid?(:save_step1)\n      # Save or proceed to the next step\n      @your_model.save!\n    else\n      render :step1\n    end\n  end\n\n  def step2\n    @your_model = YourModel.find(params[:id])\n    @your_model.assign_attributes(step2_params)\n    if @your_model.valid?(:save_step2)\n      # Save or proceed to the next step\n      @your_model.save!\n    else\n      render :step2\n    end\n  end\n\n  private\n\n  def step1_params\n    params.require(:your_model).permit(:field1, :field2)\n  end\n\n  def step2_params\n    params.require(:your_model).permit(:field3)\n  end\nend