# app/services/twitter_search_service.rb\n   require 'net/http'\n   require 'json'\n\n   class TwitterSearchService\n     def self.run(search_term = '', last_id = 0)\n       url = URI.parse("https://api.twitter.com/1.1/search/tweets.json?q=#{CGI.escape(search_term)}&since_id=#{last_id.to_s}&oauth_token=YOUR_OAUTH_TOKEN")\n       \n       response = Net::HTTP.get(url)\n       \n       JSON.parse(response.body)\n     rescue => e\n       Rails.logger.error("Error fetching tweets: #{e.message}")\n       []\n     end\n   end