# app/controllers/products_controller.rb\nclass ProductsController < ApplicationController\n  def rate\n    @product = Product.find(params[:id])\n    @product.update(rating: params[:score]) # ensure you have a rating column in your products table\n\n    respond_to do |format|\n      format.js { render json: { success: true, message: 'Rating updated successfully' } }\n      format.html\n    end\n  end\nend