class ConceptsController < ApplicationController\n\n  def create\n    @concept = Concept.new(concept_params)\n    update_status_based_on_consultation(@concept)\n    # other logic for create action\n  end\n\n  def update\n    @concept = Concept.find(params[:id])\n    @concept.assign_attributes(concept_params)\n    update_status_based_on_consultation(@concept)\n    # other logic for update action\n  end\n\n  private\n\n  def concept_params\n    params.require(:concept).permit(:consulted_legal, :consulted_marketing, ...)\n  end\n\n  def update_status_based_on_consultation(concept)\n    case [concept.consulted_legal, concept.consulted_marketing]\n    when [0, 1]\n      concept.status = 'Awaiting Compliance Approval'\n    when [1, 0]\n      concept.status = 'Awaiting Marketing Approval'\n    when [0, 0]\n      concept.status = 'Awaiting Marketing & Legal Approval'\n    else\n      concept.status = 'Pending Approval'\n    end\n  end\nend