<%= form_with(model: [@user, @publicprofile], local: true) do |f| %>\n  <%= f.label :some_label, 'Some Field:' %>\n  <%= f.text_field :some_field %>\n\n  <h3>Emails</h3>\n  <%= f.fields_for :emails do |email_form| %>\n    <%= email_form.label :email, 'Email:' %>\n    <%= email_form.email_field :email %>\n    <%= link_to_remove_fields "Remove Email", email_form %>\n  <% end %>\n  <%= link_to_add_fields "Add Email", email_form, :emails %>\n\n  <h3>Phone Numbers</h3>\n  <%= f.fields_for :phonenumbers do |phonenumber_form| %>\n    <%= phonenumber_form.label :number, 'Number:' %>\n    <%= phonenumber_form.text_field :number %>\n    <%= link_to_remove_fields "Remove Phone Number", phonenumber_form %>\n  <% end %>\n  <%= link_to_add_fields "Add Phone Number", f, :phonenumbers %>\n\n  <%= f.submit %>\n<% end %}