<% for article in @news %>\n    <% @current_date = article.created_at.to_date %>\n    <% if @current_date != @date %>\n        <%= l(@current_date, format: :default) %> <!-- uses default date format -->\n        <!-- or specify a custom format -->\n        <%= l(@current_date, format: "%A, %-d %b %Y") %> <!-- e.g., "Sunday, 12 Apr 2009" -->\n        <% @date = @current_date %>\n    <% end %>\n<% end %>