<h1>Editing contact</h1>\n\n<%= form_for(@contact) do |f| %>\n  <%= f.error_messages %>\n\n  <p>\n    <%= f.label :name %><br />\n    <%= f.text_field :name %>\n  </p>\n\n  <p>\n    <%= f.label :email %><br />\n    <%= f.text_field :email %>\n  </p>\n\n  <p>\n    <%= f.label :phone_number %><br />\n    <%= f.text_field :phone_number %>\n  </p>\n\n  <p>\n    <%= f.submit 'Update' %>\n  </p>\n<% end %>\n\n<%= link_to 'Show', @contact %> |\n<%= link_to 'Back', contacts_path %>