function pnorm(x, mean, sd) {\n    return 0.5 * (1 + erf((x - mean) / (sd * sqrt(2))))\n}\n\nEND {\n    ...\n    \n    Tb = (bhat - b0) / seb\n    Ta = (ahat - a0) / sea\n    \n    # p-value approximation for large n using normal distribution\n    pb_t = 2 * (1 - pnorm(Tb, 0, 1))\n    pa_t = 2 * (1 - pnorm(Ta, 0, 1))\n    \n    print "p-value for beta: ", pb_t\n    print "p-value for alpha: ", pa_t\n    \n    ...\n}