<!-- app/views/your_controller/your_view.html.erb -->\n<%= form_with model: @your_model, local: true do |form| %>\n  <div class="tab-content">\n    <!-- Tab 1 -->\n    <div id="tab1" class="active-tab">\n      <%= form.label :field1 %>\n      <%= form.text_field :field1 %>\n\n      <%= form.label :field2 %>\n      <%= form.text_field :field2 %>\n    </div>\n    \n    <!-- Tab 2 -->\n    <div id="tab2" style="display: none;">\n      <%= form.label :field3 %>\n      <%= form.text_field :field3 %>\n\n      <%= form.label :field4 %>\n      <%= form.text_field :field4 %>\n    </div>\n  </div>\n  \n  <!-- Tab Navigation -->\n  <button onclick="showTab('tab1')">Tab 1</button>\n  <button onclick="showTab('tab2')">Tab 2</button>\n\n  <%= form.submit "Submit" %>\n<% end %>\n\n<script>\nfunction showTab(tabId) {\n  document.querySelectorAll('.active-tab').forEach(function(tab) {\n    tab.style.display = 'none';\n    tab.classList.remove('active-tab');\n  });\n\n  var element = document.getElementById(tabId);\n  element.style.display = '';\n  element.classList.add('active-tab');\n}\n</script>\n\n<style>\n.active-tab {\n  display: block;\n}\n</style>