<tr id="ride_<%=h ride.id %>">\n  <td colspan="4"> <!-- Use colspan to ensure the div spans all columns -->\n    <div class="animate-row" id="animate_ride_<%=h ride.id %>">\n      <%=h ride.name %>\n      <%=h ride.land %>\n      <%=h ride.height_restriction %>\n      <%= link_to_remote 'remove', \n                         :url => { :action => :destroy, :ride_id => ride.id }, \n                         :html => {:onclick => "Effect.SlideUp('animate_ride_" + ride.id.to_s + "', { duration: 0.5, afterFinish: function() { Element.remove('ride_" + ride.id.to_s + "') } }); return false;"}\n      %>\n    </div>\n  </td>\n</tr>