require 'nokogiri'\n\n# Sample XML string with a structure similar to what you might have.\nxml_string = <<-XML_STRING\n<Thing>\n  <Items>\n    <Item>\n      <Title>Sample Title</Title>\n    </Item>\n  </Items>\n</Thing>\nXML_STRING\n\n# Parse the XML document.\ndoc = Nokogiri::XML(xml_string)\n\n# Access elements using XPath or CSS selectors.\ntitle = doc.xpath('//Thing/Items/Item[1]/Title').text\nputs title