<%= select_tag :editlevel, options_from_collection_for_select(Level.all, :id, :name), onchange: "fetchQuestions(this.value)" %>\n<%= select_tag :lques, {}, { size: 10, style: 'width:200px' } %>\n\n<script type="text/javascript">\n  function fetchQuestions(levelId) {\n    $.ajax({\n      url: '/questions/fetch_by_level',\n      data: { level_id: levelId },\n      success: function(data) {\n        $('#lques').empty(); // Clear existing options\n        data.forEach(function(question) {\n          var option = $('<option></option>').attr('value', question.id).text(question.name);\n          $('#lques').append(option); // Append the new option\n        });\n      }\n    });\n  }\n</script>