<!-- app/views/students/show.html.erb -->\n<%= form_with(url: student_class_memberships_path(@student), method: :post) do |form| %>\n  <%= form.select :class_id, Class.pluck(:name, :id) %>\n  <%= form.submit "Add to class" %>\n<% end %>\n\n<%= @student.classes.each do |klass| %>\n  <%= form_with(url: student_class_membership_path(@student, klass), method: :delete) do |form| %>\n    <%= klass.name %>  \n    <%= form.submit "Remove", data: { confirm: "Are you sure?" } %>\n  <% end %>\n<% end %>