<!-- Add this script in your application.html.erb or specific view -->\n<script type="text/javascript">\nfunction replaceContent() {\n  // Replace HTML using Rails' RJS helper\n  <%= @new_content %>; // Assuming new_content is rendered via a partial or raw html_safe string\n\n  // Make the div appear after hiding it\n  Effect.Appear('your_div_id', { duration: 0.5 });\n}\n\nfunction growDiv() {\n  // Use Effect.Scale to resize the div smoothly\n  new Effect.Scale('your_div_id', 100, { scaleFromCenter: true, duration: 0.5, scaleX: false });\n}\n</script>