<%= button_tag "Click Me", onclick: "updatePartial();" %>\n\n<script>\nfunction updatePartial() {\n  $.ajax({\n    url: '<%= update_partial_path %>', // Ensure this path matches your route\n    type: 'GET',\n    dataType: 'js', // Expecting JS response (RJS)\n    success: function(data) {\n      console.log("Partial updated successfully");\n    },\n    error: function(data) {\n      console.error("Error updating partial");\n    }\n  });\n}\n</script>