require 'test_helper'\n\nclass ParentTest < ActiveSupport::TestCase\n  def setup\n    @parent = FactoryBot.create(:parent)\n  end\n\n  def test_children_association\n    expected_child_count = @parent.children.size # Get the actual number of children associated with the parent\n    assert_equal expected_child_count, @parent.children.count, "Parent.children isn't providing the right records"\n  end\nend