class ApplicationController < ActionController::Base\n  after_filter :set_secure_cookie\n\n  def set_secure_cookie\n    if response.cookies['your_session_key']\n      # Modify the Set-Cookie header to include HttpOnly\n      cookies = response.headers['Set-Cookie'].split("\n")\n      cookies.map! do |cookie|\n        case cookie\n        when /^your_session_key/\n          cookie += '; HttpOnly'\n        else\n          cookie\n        end\n      end\n      response.headers['Set-Cookie'] = cookies.join("\n")\n    end\n  end\nend