require File.dirname(__FILE__) + '/../test_helper'\nrequire 'site_controller'\n\n# Re-raise errors caught by the controller.\nclass SiteController; def rescue_action(e) raise e end; end\n\nclass SiteControllerTest < ActionController::TestCase\n  # No need to manually initialize @controller, @request, and @response.\n\n  test "should get index" do\n    get :index\n    assert_response :success\n    assert_template "index"\n    title = assigns(:title)\n    assert_not_nil title, "Title should not be nil"\n    assert_equal "Welcome to RailsSpace!", title\n  end\n\n  test "should get about" do\n    get :about\n    assert_response :success\n    assert_template "about"\n    title = assigns(:title)\n    assert_not_nil title, "Title should not be nil"\n    assert_equal "About RailsSpace", title\n  end\n\n  test "should get help" do\n    get :help\n    assert_response :success\n    assert_template "help"\n    title = assigns(:title)\n    assert_not_nil title, "Title should not be nil"\n    assert_equal "RailsSpace Help", title\n  end\nend