module MyModule\n  def function1\n    puts "Function 1"\n  end\n\n  def function2\n    puts "Function 2"\n  end\n\n  def function3\n    puts "Function 3"\n  end\nend\n\nclass MyClass\n  include MyModule\n\n  # Alias or directly reference the methods you want to use\n  alias_method :my_function1, :function1\n  alias_method :my_function2, :function2\n\n  # Un-define the method you don't want\n  undef_method :function3\nend\n\nobj = MyClass.new\nobj.my_function1  # Outputs: Function 1\nobj.my_function2  # Outputs: Function 2\n# obj.function3   # This will raise a NoMethodError