class Participant\n  def self.field_event_sort(a, b)\n    a_parts = a.time_distance.split(/[:'.]/).map(&:to_i)\n    b_parts = b.time_distance.split(/[:'.]/).map(&:to_i)\n\n    # Ensure both parts have the same length by padding with zeros\n    while a_parts.length < 3; a_parts.append(0); end\n    while b_parts.length < 3; b_parts.append(0); end\n\n    # Compare arrays directly for field events (longest first)\n    b_parts <=> a_parts\n  end\nend