# WEB-INF/ruby/my_rack_app.rb\nclass MyRackApp\n    def initialize\n        @script_root = File.expand_path('../ruby', __FILE__)\n    end\n\n    def call(env)\n        script_name = env['PATH_INFO'].sub(/^\//, '')\n        script_path = File.join(@script_root, "#{script_name}.rb")\n\n        if File.exist?(script_path)\n            # Load and execute the script\n            output = `jruby #{script_path} #{env['QUERY_STRING']}`\n            return [200, {'Content-Type' => 'text/html'}, [output]]\n        else\n            return [404, {'Content-Type' => 'text/plain'}, ['File not found']]\n        end\n    rescue => e\n        return [500, {'Content-Type' => 'text/plain'}, ["An error occurred: #{e}"]]\n    end\nend