# In your Rails routes file (config/routes.rb)\nnamespace :api do\n  namespace :v1 do\n    resources :data_points, only: [:create]\n  end\nend\n\n# Controller for handling data points in app/controllers/api/v1/data_points_controller.rb\nmodule Api\n  module V1\n    class DataPointsController < ApplicationController\n      def create\n        data_point = DataPoint.new(data_point_params)\n        if data_point.save\n          render json: { message: 'Data saved successfully' }, status: :created\n        else\n          render json: { errors: data_point.errors.full_messages }, status: :unprocessable_entity\n        end\n      end\n\n      private\n\n      def data_point_params\n        params.require(:data_point).permit(:value, :timestamp)\n      end\n    end\n  end\nend