class PdfGenerationJob < ApplicationJob\n     queue_as :default\n\n     def perform(template_path, output_pdf_path)\n       template = File.read(template_path)\n       content = ERB.new(template).result(binding)\n       File.write('output.tex', content)\n\n       # Compile to PDF\n       system("pdflatex -output-directory=#{File.dirname(output_pdf_path)} #{'output.tex'}")\n     end\n   end