setup do\n  post :create, params: { user: Factory.attributes_for(:user) }\n  @created_user = assigns(:user)\nend\n\ntest "should create a new user" do\n  assert_difference 'User.count', 1 do\n    setup\n  end\n\n  # Check for validation errors if the record was not created\n  unless @response.successful?\n    puts "Errors: #{@created_user.errors.full_messages.inspect}"\n  end\nend