# In config/routes.rb\nRails.application.routes.draw do\n  resources :events, only: [:index, :show]\nend\n\n# In app/controllers/events_controller.rb\nclass EventsController < ApplicationController\n  before_action :set_event, only: [:show]\n\n  def index\n    @events = Event.where(filter_params).limit(100)\n    render json: @events\n  end\n\n  def show\n    render json: @event\n  end\n\n  private\n\n  def set_event\n    @event = Event.find(params[:id])\n  rescue ActiveRecord::RecordNotFound => e\n    render status: :not_found, json: { error: "Event not found" }\n  end\n\n  def filter_params\n    params.permit(:source_id, :start_date, :end_date)\n  end\nend