require 'sinatra'\n    require 'sequel'\n\n    # Connect to an SQLite database (or another one like PostgreSQL)\n    DB = Sequel.connect('sqlite://my_database.db')\n\n    # Create a database schema if needed\n    DB.create_table? :items do\n      primary_key :id\n      String :name, null: false\n      Integer :value, default: 0\n      DateTime :created_at, default: Sequel.now\n    end\n\n    # Define an Item class that maps to the `items` table\n    class Item < Sequel::Model\n    end\n\n    # Route definitions\n    get '/items' do\n      @items = Item.all\n      erb :index\n    end\n\n    post '/items' do\n      name = params[:name]\n      value = params[:value].to_i\n\n      Item.create(name: name, value: value)\n\n      redirect to('/items')\n    end