class PasswordController < ApplicationController\n  def encrypt_password\n    password = params[:password]\n    encrypted_password = EncryptionJava.encrypt(password)\n    render plain: "Encrypted Password: #{encrypted_password}"\n  rescue => e\n    render plain: "Error: #{e.message}", status: :unprocessable_entity\n  end\n\n  def decrypt_password\n    encrypted_password = params[:encrypted_password]\n    decrypted_password = EncryptionJava.decrypt(encrypted_password)\n    render plain: "Decrypted Password: #{decrypted_password}"\n  rescue => e\n    render plain: "Error: #{e.message}", status: :unprocessable_entity\n  end\nend