# Second approach: Using ActiveRecord-JDBC\n\nrequire 'rubygems'\ngem 'activerecord-jdbc-adapter'\nrequire 'jdbc/oracle'\nrequire 'active_record'\n\nActiveRecord::Base.establish_connection(\n  adapter: 'oracle_enhanced',\n  driver: 'oracle.jdbc.driver.OracleDriver',\n  url: 'jdbc:oracle:thin:@myhost:1521:mydb',\n  username: 'myuser',\n  password: 'mypassword'\n)\n\n# Test query\nbegin\n  results = ActiveRecord::Base.connection.execute("SELECT * FROM mytable")\n  results.each do |row|\n    p row\n  end\nrescue => e\n  puts "Error executing query: #{e.message}"\nend