# First approach: using JDBC directly with JRuby\n\nrequire 'java'\nrequire 'rubygems'\n\n# Ensure you have jdbc-oracle gem installed\ngem 'jdbc-oracle'\nrequire 'jdbc/oracle'\n\n# Establish connection\nurl = "jdbc:oracle:thin:@myhost:1521:mydb"\nusername = 'myuser'\npassword = 'mypassword'\n\nbegin\n  con = java.sql.DriverManager.getConnection(url, username, password)\n  if con\n    puts "Connection good"\n  else\n    puts "Connection failed"\n  end\nrescue => e\n  puts "Error connecting to database: #{e.message}"\nend