def destroy\n  @ccj = ContactCompanyJoin.find(params[:id])\n  contact_id = @ccj.contact_id\n  company_id = @ccj.company_id\n\n  if @ccj.destroy\n    respond_to do |format|\n      format.html { redirect_back(fallback_location: root_path, notice: 'Connection was successfully destroyed.') }\n      format.js { render partial: "contact_company_joins/remove_join", locals: { contact_id: contact_id, company_id: company_id } }\n    end\n  else\n    respond_to do |format|\n      format.html { redirect_back(fallback_location: root_path, alert: 'Failed to destroy connection.') }\n      format.js   { render partial: "contact_company_joins/remove_join_error" }\n    end\n  end\nend