def whereami(method_name, klass)\n  method = klass.instance_method(method_name)\n  if method.source_location\n    puts "#{method_name} is defined in #{method.source_location.join(':')}"\n  else\n    puts "#{method_name} does not have a Ruby source location"\n  end\nrescue NameError => e\n  puts "Method #{method_name} not found in #{klass}: #{e.message}"\nend\n\n# Usage example:\nwhereami(:to_s, String) # Replace :to_s and String with your desired method name and class/module