require 'nokogiri'\nrequire 'ostruct'\n\n# Sample XML string.\nxml_string = <<-XML_STRING\n<Thing>\n  <Items>\n    <Item>\n      <Title>Sample Title</Title>\n    </Item>\n  </Items>\n</Thing>\nXML_STRING\n\n# Parse the XML document.\ndoc = Nokogiri::XML(xml_string)\n\n# Convert to OpenStruct objects.\nthing_data = {\n  items: doc.xpath('//Thing/Items/Item').map do |item|\n    OpenStruct.new(title: item.xpath('Title').text)\n  end\n}\n\nthing = OpenStruct.new(thing_data)\n\n# Access elements like Ruby objects.\nputs thing.items[0].title