# Load Rake and create a new application instance to simulate running Rake tasks\nrequire 'rake'\n@rake = Rake::Application.new\nRake.application = @rake\n\n# Define the :environment task with your test database setup\nnamespace :db do\n  desc "Load the test environment"\n  task :load_test_environment => :environment do\n    # Set up database connection for tests here\n    ENV['RAILS_ENV'] = 'test'\n    require File.expand_path('../../environment', __FILE__)\n    ActiveRecord::Base.establish_connection(ActiveRecord::Base.configurations['test'])\n  end\n\n  Rake::Task[:environment].prerequisites.delete(:load_environment)\n  task :environment => [:load_test_environment] do\n    # Additional environment setup if needed\n  end\nend\n\n# Load your tasks after defining the overridden :environment task\nload File.dirname(__FILE__) + '/../../lib/tasks/do_something.rake'