require "gtk2"\n\n# Adds a page to the notebook with the given label\ndef create_page(notebook, label = "untitled")\n  # Create a textview\n  text_view = Gtk::TextView.new\n  # Append it to the notebook\n  notebook.append_page(text_view, Gtk::Label.new(label))\nend\n\nGtk.init\nwindow = Gtk::Window.new(Gtk::WINDOW_TOPLEVEL)\nwindow.set_default_size(800, 600)\nwindow.signal_connect("destroy") { Gtk.main_quit }\n\ncontainer = Gtk::VBox.new\nnotebook = Gtk::Notebook.new\nbutton = Gtk::Button.new("New")\n\n# When I push the button, a new page should be added\nbutton.signal_connect("clicked") do\n  create_page(notebook)\nend\n\ncontainer.pack_start(button, false, false, 5) # Add some padding for aesthetics\ncreate_page(notebook)\ncontainer.pack_start(notebook, true, true, 0)\n\nwindow.add(container)\nwindow.show_all\n\nGtk.mai