require 'ruby-prof'\n  \n  Given("the page loads with expensive calculations") do\n    # Start the profiling\n    RubyProf.start\n    \n    # The code to profile\n    visit("/projects/by/114951412")\n    \n    # Stop the profiling and get the results\n    result = RubyProf.stop\n  \n    # Print a flat profile to text\n    printer = RubyProf::FlatPrinter.new(result)\n    printer.print(STDOUT, 0)\n  end