# spec/lib/tasks/my_namespace_spec.rb\nrequire 'rake'\nload File.expand_path('../../../../lib/tasks/my_namespace.rake', __FILE__)\ninclude MyNamespace\n\nRSpec.describe "my_namespace:cleanup" do\n  include_context "rake"\n\n  it "performs cleanup operations" do\n    # Assuming your task updates a model and sets all fields to nil\n    thing = create(:thing, data: 'some data')\n    expect(thing.data).to eq('some data')\n\n    subject.invoke\n\n    thing.reload\n    expect(thing.data).to be_nil\n  end\nend