module AuthenticationHelper\n  def login_with_credentials(type)\n    if type == "valid"\n      # logic to log in with valid credentials\n    elsif type == "invalid"\n      # logic to log in with invalid credentials\n    else\n      raise ArgumentError, "Unknown credential type: #{type}"\n    end\n  end\n\n  def login_successfully\n    login_with_credentials("valid")\n  end\nend\n\nWorld(AuthenticationHelper)\n\nGiven /^I login with (.*) credentials$/ do |type|\n  login_with_credentials(type)\nend\n\nGiven /^I login successfully$/ do\n  login_successfully\nend