require 'net/smtp'\nrequire 'openssl'\n\nmessage = <<~MESSAGE_END\nFrom: Private Person <me@gmail.com>\nTo: A Test User <test@example.com>\nSubject: SMTP email test\n\nThis is the body of the email.\nMESSAGE_END\n\nNet::SMTP.start('smtp.gmail.com', 587, 'localhost', 'your_email@gmail.com', 'your_password', :login) do |smtp|\n  smtp.enable_starttls_auto\n  smtp.ssl_context = OpenSSL::SSL::SSLContext.new(OpenSSL::SSL::Constants::TLSv1_2)\n  smtp.send_message message, 'me@gmail.com', 'test@example.com'\nend