class YourRemoteResource < ActiveResource::Base\n  self.site = "http://api.example.com"\n\n  # Override new? method to consider a resource with an id as new if you want to create it explicitly\n  def new?\n    false # This will force the use of update instead of create\n  end\n\n  # Custom save method\n  def explicit_save\n    response = connection.post(custom_element_path(id), encode, self.class.headers)\n    load_attributes_from_response(response)\n  end\n\n  private\n\n  # Define a custom path for creating with an ID if needed\n  def custom_element_path(id)\n    "#{self.class.collection_name}/#{id}"\n  end\nend