# File: lib/tasks/export_to_yaml.rake\n\nnamespace :db do\n  desc "Export a database table to a YAML fixture file"\n  task :export_table_to_yaml => :environment do\n    require 'yaml'\n    \n    # Specify your model/table name here\n    model_name = "YourModelName"\n    model_klass = Kernel.const_get(model_name)\n    \n    # Fetch all records from the specified table\n    records = model_klass.all\n    \n    # Convert array of records to hash with ids as keys and record attributes as values\n    fixture_data = Hash[records.map { |r| [r.id, r.attributes.except('created_at', 'updated_at')] }]\n    \n    # Write the data to a YAML file in the test/fixtures directory\n    File.open(Rails.root.join("test", "fixtures", "#{model_name.tableize}.yml"), 'w') do |file|\n      file.write fixture_data.to_yaml\n    end\n    \n    puts "Exported #{records.count} records from table '#{model_klass.table_name}' to '#{Rails.root.join("test", "fixtures", "#{model_name.tableize}.yml")}'."\n  end\nend