# Ruby 1.8 syntax for hash with symbols as keys\nhash_old = { :name => "Alice", :age => 25 }\n\n# Ruby 1.9 new syntax for hash with symbols as keys\nhash_new = { name: "Alice", age: 25 }\n\n# Ruby 1.8 block local variable\n[1,2,3].each do |num; total|\n  # use num and total here\nend\n\n# Ruby 1.9 block local variable\n[1,2,3].each do |num,; total|\n  # use num and total here\nend