require 'axlsx'\n\ndef export_to_xlsx(data)\n  Axlsx::Package.new do |p|\n    p.workbook.add_worksheet(name: "Sheet1") do |sheet|\n      # Add headers if data is a hash of rows\n      if data.first.is_a?(Hash)\n        sheet.add_row data.first.keys.map(&:to_s)\n        data.each do |row|\n          sheet.add_row row.values\n        end\n      else\n        # Directly add rows for an array of arrays\n        data.each do |row|\n          sheet.add_row row\n        end\n      end\n    end\n    p.serialize(File.expand_path("output.xlsx"))\n  end\nend\n\n# Example usage:\ndata = [\n  { name: "Jürgen", age: 30 },\n  { name: "María", age: 29 }\n]\nexport_to_xlsx(data)